import React from 'react';
import { StyleSheet, View, Text, ScrollView } from 'react-native';
import { useRoute } from '@react-navigation/native';

import Icon from 'react-native-vector-icons/Entypo';
import { colors, fonts } from '../../styles';

import { Button, RadioGroup, Dropdown } from '../../components';

export default function ComponentsScreen(props) {
  const route = useRoute();
  return (
    <ScrollView
      style={styles.container}
      contentContainerStyle={{ paddingBottom: 20 }}
    >
      <View style={styles.componentsSection}>
        <Text style={styles.componentSectionHeader}>Radio Group</Text>

        <RadioGroup
          style={styles.demoItem}
          items={['One', 'Two', 'Three']}
          selectedIndex={props.radioGroupsState[0]}
          onChange={index =>
            props.setRadioGroupsState({ ...props.radioGroupsState, 0: index })
          }
        />

        <View
          style={{
            height: 1,
            alignSelf: 'stretch',
            backgroundColor: colors.bluish,
          }}
        />

        <RadioGroup
          underline
          style={styles.demoItem}
          items={['One', 'Two', 'Three', 'Four']}
          selectedIndex={props.radioGroupsState[1]}
          onChange={index =>
            props.setRadioGroupsState({ ...props.radioGroupsState, 1: index })
          }
        />
      </View>

      <View style={styles.componentsSection}>
        <Text style={styles.componentSectionHeader}>Buttons</Text>

        <View style={styles.demoButtonsContainer}>
          <Button
            style={[styles.demoButton, {flexBasis: '47%'}]}
            primary
            caption="Button"
            onPress={() => {}}
          />
          <Button
            style={[styles.demoButton, {flexBasis: '47%'}]}
            secondary
            caption="Button"
            onPress={() => {}}
          />
          <Button
            style={[styles.demoButton, {flexBasis: '47%'}]}
            primary
            rounded
            caption="Button"
            onPress={() => {}}
          />
          <Button
            style={[styles.demoButton, {flexBasis: '47%'}]}
            secondary
            rounded
            caption="Button"
            onPress={() => {}}
          />
          <Button
            style={[styles.demoButton, {flexBasis: '47%'}]}
            primary
            bordered
            caption="Button"
            onPress={() => {}}
          />
          <Button
            style={[styles.demoButton, {flexBasis: '47%'}]}
            secondary
            bordered
            caption="Button"
            onPress={() => {}}
          />
          <Button
            style={[styles.demoButton, {flexBasis: '47%'}]}
            primary
            bordered
            rounded
            caption="Button"
            onPress={() => {}}
          />
          <Button
            style={[styles.demoButton, {flexBasis: '47%'}]}
            secondary
            bordered
            rounded
            caption="Button"
